catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    var taskDet = new GlideAjax('x_665266_mypdiapps.dataUpdateUtility');
    taskDet.addParam('sysparm_name', 'getTaskDetails');
    taskDet.addParam('sysparm_taskNum', g_form.getValue('exact_task_number'));
    taskDet.getXML(ajaxResp);

    function ajaxResp(response) {
        // get attachmentInfo elements
        var ans = response.responseXML.documentElement.getAttribute('answer');
        var answer = JSON.parse(ans);
        //alert(answer.sId);
        g_form.setValue('task_ref_record', answer.sId);
        g_form.setValue('task_class', answer.sysClass.toString());
        if (typeof(Storage) !== "undefined") {
            sessionStorage.setItem("class", "" + g_form.getValue('task_class'));
        } else {
            alert("Sorry, your browser does not support Web Storage...");
        }
    }

    //Type appropriate comment here, and begin script below

}
