function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
	var record = sessionStorage.getItem("record");
	var cls = sessionStorage.getItem("class");
	var fieldVal = new GlideAjax('x_665266_mypdiapps.dataUpdateUtility');
    fieldVal.addParam('sysparm_name', 'getValues');
	fieldVal.addParam('sysparm_cls', cls);
    fieldVal.addParam('sysparm_rec', record);
	fieldVal.addParam('sysparm_field', g_form.getValue('field'));
    fieldVal.getXML(ajaxResp);

	function ajaxResp(response) {
        // get attachmentInfo elements
        var ans = response.responseXML.documentElement.getAttribute('answer');
		g_form.setValue('current_value',ans);
	}
   //Type appropriate comment here, and begin script below

}
