catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        g_form.setDisplay('exact_task_number', false);
        g_form.setDisplay('enter_ci_name', false);
        g_form.setDisplay('task_ref_record', false);
        g_form.setDisplay('ref_ci_record', false);
        g_form.setDisplay('task_class', false);
        g_form.setDisplay('ci_class', false);
        g_form.setDisplay('task_fields', false);
        return;
    }

    if (g_form.getValue('record_class') == 'task') {
        g_form.setDisplay('exact_task_number', true);
        g_form.setDisplay('task_ref_record', true);
        g_form.setDisplay('task_class', true);
        g_form.setDisplay('task_fields', true);
		g_form.setDisplay('enter_ci_name', false);
        g_form.setDisplay('ref_ci_record', false);
        g_form.setDisplay('ci_class', false);
    }
    if (g_form.getValue('record_class') == 'ci') {
        g_form.setDisplay('enter_ci_name', true);
        g_form.setDisplay('ref_ci_record', true);
        g_form.setDisplay('ci_class', true);
        g_form.setDisplay('task_fields', true);
		g_form.setDisplay('exact_task_number', false);
        g_form.setDisplay('task_ref_record', false);
        g_form.setDisplay('task_class', false);
    }
    //Type appropriate comment here, and begin script below
}
