sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  try {
    var idRitm = inputs.requested_item;
    gs.info("Data Modification : " + idRitm);
    //var ritm_id = inputs.requested_item;
    var ritm = new GlideRecord('sc_req_item');
    ritm.get(idRitm);
    var task = ritm.variables.task_ref_record;
    var cls = ritm.variables.task_class;
    var mrvs = ritm.variables.task_fields;
    var mrVars = global.JSON.parse(mrvs);
	var field_arr = [];
    for (var i = 0; i < mrvs.getRowCount(); i++) {
        var plcArr = {};
        var fld = new GlideRecord('sys_dictionary');
        fld.addEncodedQuery("nameIN" + cls + "^column_labelSTARTSWITH" + mrVars[i].field);
        fld.query();
        if (fld.next()) {
            plcArr.fieldName = fld.element.toString();
            plcArr.fieldVal = mrVars[i].new_value.toString();
            field_arr.push(plcArr);
        }
    }
    var json = new global.JSON();
    var tskDta = json.encode(field_arr);
    var fieldValArr = global.JSON.parse(tskDta);

    var tskRec = new GlideRecord('task');
    tskRec.get(task);

    var tskRefRec = new GlideRecord(tskRec.sys_class_name);
    tskRefRec.get(task);

    for (var j = 0; j < fieldValArr.length; j++) {
        tskRefRec.setValue(fieldValArr[j].fieldName, fieldValArr[j].fieldVal);
    }
    tskRefRec.update();
} catch (ex) {
    gs.info('Data exception: ' + ex);
    var message = ex.getMessage();
    gs.error(message);
}


})(inputs, outputs);

