sys_script_include.script:
var dataUpdateUtility = Class.create();
dataUpdateUtility.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getTaskDetails: function() {
        var task = this.getParameter('sysparm_taskNum');
        var resp = {};
        var taskGR = new GlideRecord('task');
        taskGR.addEncodedQuery('numberLIKE' + task);
        taskGR.query();
        if (taskGR.next()) {

            resp.sId = taskGR.sys_id.toString();
            resp.sysClass = getClasses(taskGR.sys_class_name);
        } else {
            resp.error = 'No Matching Record found.';
        }
        var json = new global.JSON();
        var tskDta = json.encode(resp); //JSON formatted string    
        return tskDta;

        function getClasses(cls) {
            var arr = [];
            arr.push(cls);
            return checkChildExists(cls);

            function checkChildExists(c) {
                var ch = new GlideRecord('sys_db_object');
                ch.addQuery('name', c);
                ch.query();
                if (ch.next()) {
                    arr.push(ch.super_class.name + '');
                    checkChildExists(ch.super_class.name);
                }
                var str = arr.toString();
                return str;
            }
        }

    },

    getCiDetails: function() {
        var ci = this.getParameter('sysparm_ci');
        var resp = {};
        var ciGR = new GlideRecord('cmdb_ci');
        ciGR.addEncodedQuery('nameLIKE' + ci);
        ciGR.query();
        if (ciGR.next()) {
            resp.sId = ciGR.sys_id.toString();
            resp.sysClass = ciGR.sys_class_name.getDisplayValue();
        } else {
            resp.error = 'No Matching Record found.';
        }
        var json = new global.JSON();
        var ciDta = json.encode(resp); //JSON formatted string    

        return ciDta;
    },

    getValues: function() {
        var cls = this.getParameter('sysparm_cls');
        var rec = this.getParameter('sysparm_rec');
        var field = '';
		var recCls = '';
        var dic = new GlideRecord('sys_dictionary');
        dic.addEncodedQuery('nameIN' + cls + '^column_labelSTARTSWITH' + this.getParameter('sysparm_field'));
        dic.query();
        if (dic.next()){
			field = dic.element;
			recCls = dic.name
		}


        var grRec = new GlideRecord(recCls);
        grRec.get(rec);
        return grRec.getDisplayValue(field);

    },

    type: 'dataUpdateUtility'
});
