sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
    var payload = _processVariable(request.getBody);
    _submitReq(payload);

    function _processVarible(js) {
        var key = '';
        var variables = js['variables'];
        for (key in variables) {
            if (variables.hasOwnProperty(key)) {
                var varType = _checkVariableType(key, js['sysparm_id']);
                if (varType['itemType'] == 'reference') {
                    _getDisplayValue(variables[key], varType['refCls']);
                }
            }
        }

    }

    function _checkVariableType(variable, cat) {
        var varDet = {};
        var item = new GlideRecord('item_option_new');
        item.addEncodedQuery('cat_item=' + cat + '^name=' + variable);
        item.query();
        if (item.next()) {
            if (item.type == '8') {
                varDet.itemType = 'reference';
                varDet.refCls = item.reference;
            }
        }
        return varDet;
    }

    function _getDisplayValue(att, cls) {
        var refRec = new GlideRecord(cls);
        refRec.get('number', att);
        return refRec.sys_id;
    }

    function _submitReq(payload) {
        var cart = new sn_sc.CartJS();
        cart.setRequestedFor('f81782352fa321106cc8bea62799b657');
        var cartDetails = cart.orderNow(payload);
        response.setBody = cartDetails.request_number;
    }
    /*var item = {
                'sysparm_id': '4f1212d02f2321106cc8bea62799b68f',
                'sysparm_quantity': '1',
                'variables': {
                    'record_class': 'change_request',
                    'exact_task_number': 'CHG0000001',
                    'task_class': 'change_request',
                }

            };*/
})(request, response);
